import React, { Component } from 'react';
import CustomModal from './components/Modal';
import './App.css';

const tasks = [
  {
    id: 1,
    title: "Learn React",
    description: "Learn how to use ReactJS",
    completed: false
  },
  {
    id: 2,
    title: "Learn Redux",
    description: "Learn how to use Redux",
    completed: false
  },
  {
    id: 3,
    title: "Learn React Router",
    description: "Learn how to use React Router",
    completed: false
  }
];









class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      modal: false,
      viewCompleted: false,
      taskList: tasks,
      activeItem: {
        title: "",
        description: "",
        completed: false
    },
    tasklist: tasks,     
  };
}

//create toggle property
toggle = () => {
  this.setState({ modal: !this.state.modal });
};
handleSubmit = item => {
  this.toggle();
  alert('Saved!' + JSON.stringify(item));
}
handleDelete = item => {
  alert('SaveDeleted!' + JSON.stringify(item));
}

createItem = () => {
  const item = { title: "", description: "", completed: false };
  this.setState({ activeItem: item, modal: !this.state.modal });
}


displayCompleted = status => {

  if (status) {
    return this.setState({ viewCompleted: true });
  }
  return this.setState({ viewCompleted: false });

}

renderTabList = () => {
  return (
    <div className="my-5 tab-list">
      <span
        onClick={() => this.displayCompleted(true)}
        className={this.state.viewCompleted ? "active" : ""}
      >
        Completed
      </span>
      <span
        onClick={() => this.displayCompleted(false)}
        className={this.state.viewCompleted ? "" : "active"}
      >
        Incomplete
      </span>
    </div>
  );
}


// REndering items in the list = compl/incopmlete
renderItems = () => {
  const{ viewCompleted } = this.state;
  const newItems = this.state.taskList.filter(
    item => item.completed == viewCompleted
  );

return newItems.map(item => (
  <li
    key={item.id}
    className="list-group-item d-flex justify-content-between align-items-center"
  >
    <span
      className={`todo-title mr-2 ${
        this.state.viewCompleted ? "completed-todo" : ""
      }`}
      title={item.description}
    >
      {item.title}
    </span>
    <span>
      <button className="btn btn-secondary mr-2"> Edit </button>
      <button className="btn btn-danger">Delete </button>
    </span>
  </li>
  ))
};  

render() {
  return (
    <main className="content">
      <h1 className="text-black text-uppercase text-center my-4">Todo app</h1>
      <h1 className="row">
        <div className="col-md-6 col-sm-10 mx-auto p-0">
          <div className="card p-3">
            <div>
              <button className="btn btn-primary">Add task</button>
            </div>
            {this.renderTabList()}
            <ul className="list-group list-group-flush">
              {this.renderItems()}
            </ul>
          </div>
        </div>
      </h1>


    </main>
  )
}};



export default App;
